import React from "react";
import ButtonCustom from "../../components/button";
import "./index.css";

export const Main = () => {
    const [inputValue, setInputValue] = React.useState(""); // HOOK. primero pongo el estado, despues el metodo que actualiza el estado y por ultimo el valor inicial.

    const handleClick = () => {
        alert("apretaste" + inputValue);
    };

    const handleValue = (event) => {
        console.log(event.target.value);
    };

    return (
        //el return siempre tiene que estar encapsulado dentro de las <> </>
        <>
            <div className="contenedor">
                <p>Esto es una mierda</p>
            </div>
            <ButtonCustom handleClick={handleClick}></ButtonCustom>
            <input onChange={handleValue}></input>
        </>
    );
};
